var db = require('dbHelper');
var login = function(userId,password,onSuccess,onFailure){
	db.query("select * from users where id='"+userId+"' and password='"+password+"'",function(error,results){
		if(error || results.length==0){
			onFailure();
		}else{
			onSuccess();
		}
	});
}
